mapper.update:
update ${tableName}
set depart_no = #{depart.departNo,jdbcType=VARCHAR},
  depart_name = #{depart.departName,jdbcType=VARCHAR},
  parent_no = #{depart.parentNo,jdbcType=VARCHAR},
  node_path = #{depart.nodePath,jdbcType=VARCHAR},
  isLeaf = #{depart.isleaf,jdbcType=BIT},
  status = #{depart.status,jdbcType=BIT}
where id = #{depart.id,jdbcType=VARCHAR}
